import {current} from "immer";
import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "./Weather.module.css";
import {
    fetchCurrentWeather,
} from "./weatherSlice";

const Weather = () => {
	const [city,setCity] = useState('Yangon')
    const {current,loading,error} = useSelector(state => state.weather)
    const dispatch = useDispatch()

    const onCityChanged = (e) => {
        setCity(e.target.value)
    }

    const onSubmit = () => {
        dispatch(fetchCurrentWeather(city))
    }

    useEffect(() => {
        dispatch(fetchCurrentWeather(city))
     }, [dispatch]);


    const showCurrentWeather = () => (
        <>
            <div>
                {JSON.stringify(current.weather[0].main)}
                {JSON.stringify(current.weather[0].icon)}
                {JSON.stringify(current.weather[0].id)}
            </div>
            <h1>{city} {JSON.stringify(current.weather[0].description)} { current && current.main.temp} &#176;</h1>
            <h6>{}</h6>
            <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
            <button className={styles.form__submit} onClick={onSubmit}>Search</button>
        </>
    )
    const showForecastWeather = () => (
        <>
            <div>
                {JSON.stringify(current.weather[0].main)}
                {JSON.stringify(current.weather[0].icon)}
                {JSON.stringify(current.weather[0].id)}
            </div>
            <h1>{city} {JSON.stringify(current.weather[0].description)} { current && current.main.temp} &#176;</h1>
            <h6>{}</h6>
            <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
            <button className={styles.form__submit} onClick={onSubmit}>Search</button>
        </>
    )
	return (
		<div>  
            {
                current && !loading && !error &&
                showCurrentWeather() &&
                showForecastWeather()
            }
            {
                loading && 
                <div>Loading .....</div>
            }
            {
                error && 
                <div>Data not found ...!</div>
            }
        </div>
	)
}

export default Weather
