import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import moment from "moment";
import styles from "./Weather.module.css";
import {
    fetchCurrentWeather,
    fetchWeatherForecast
} from "./weatherSlice";

const Weather = () => {
	const [city,setCity] = useState('Yangon')
    const {current,loading,error,forecast} = useSelector(state => state.weather)
    const dispatch = useDispatch()
    const today = moment().format('dddd, MMMM Do YYYY')

    const onCityChanged = (e) => {
        setCity(e.target.value)
    }

    const onSubmit = (e) => {
        e.preventDefault()
        dispatch(fetchCurrentWeather(city))
    }

    useEffect(() => {
        dispatch(fetchCurrentWeather(city)).then(res => {
            const {coord} = res.payload.data
            dispatch(fetchWeatherForecast(coord))

        })
        // dispatch(fetchWeatherForecast(city))
     }, [dispatch]);

     const showSearchBox = () => (
       <div>
            <form onSubmit={onSubmit}>
                <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
                {/* <button className={styles.form__submit} onClick={onSubmit}>Search</button> */}
            </form>
       </div>
     )
    const showCurrentWeather = () => (
        <div>
            <h2>{current.name}</h2>
            <img className={styles.current__icon} src={`http://openweathermap.org/img/wn/${current.weather[0].icon}@2x.png`} alt="weather"></img>
            <h1>{ current && current.main.temp} &#176;</h1>
            <div className={styles.current__weather}>
                <h3>{current.weather[0].description}</h3>
            </div>
        </div>
    )
    const showForecastWeather = () => (
        <>
            <div className={styles.forecast__container}>
                {/* {JSON.stringify(forecast)} */}
                {
                    forecast.map((item,index) => (
                        <div className={styles.forecast__daily} key={index}>
                            <p>{moment().add(index+1, 'day').format('ddd')}</p>
                            <img className={styles.forecast__icon} src={`http://openweathermap.org/img/wn/${item.weather[0].icon}@2x.png`} alt="weather"></img>
                            <p>{item.temp.day} &#176;</p>
                        </div>
                    ))
                }
            </div>
        </>
    )
	return (
		<div className={styles.background}>  
            <div className={styles.date}>
                {today}
            </div>
            {
                showSearchBox()
            }
            {
                current && !loading && !error &&
                showCurrentWeather()
            }
            {
                forecast && !loading && !error &&
                showForecastWeather()
            }
            {
                loading && 
                <div>Loading .....</div>
            }
            {
                error && 
                <div>Data not found for {city}...!</div>
            }
        </div>
	)
}

export default Weather
