import {current} from "immer";
import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "./Weather.module.css";
import {
    fetchCurrentWeather,
} from "./weatherSlice";

const Weather = () => {
	const [city,setCity] = useState('Yangon')
    const current = useSelector(state => state.weather.current)
    const dispatch = useDispatch()

    const onCityChanged = (e) => {
        setCity(e.target.value)
    }

    const onSubmit = () => {
        dispatch(fetchCurrentWeather(city))
    }

    useEffect(() => {
        dispatch(fetchCurrentWeather(city))
     }, [dispatch, city]);


	return (
		<div>  
            <div>
                {
                    current && 
                    JSON.stringify(current.main.temp)
                }
            </div>
            <h1>{city} { current && current.main.temp} &#176;</h1>
            <h6>{}</h6>
            <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
            <button className={styles.form__submit} onClick={onSubmit}>Search</button>
        </div>
	)
}

export default Weather
