import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import moment from "moment";
import Loader from '../loader/Loader';
import CurrentWeather from '../current/Current';
import DailyWeather from '../daily/Daily';
import logo from '../../images/logo.png'
import styles from "./Weather.module.css";

import {
    fetchCurrentWeather,
    fetchWeatherForecast,
    weatherAction
} from "./weatherSlice";

const Weather = () => {

	const [city,setCity] = useState('Yangon')
    const {current,loading,error,forecast} = useSelector(state => state.weather)
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(fetchCurrentWeather(city))
        .then(res => {
            const {coord} = res.payload.data
            dispatch(fetchWeatherForecast(coord))
        })
        .catch(err => {
            dispatch(weatherAction.setError(true))
        })
     }, [dispatch,city]);


    //  methods

    const unixFormatter = (dt,format) => {
        return moment(new Date(dt*1000)).format(format)
    }

    const onCityChanged = (e) => {
        setCity(e.target.value)
    }

    const onSubmit = (e) => {
        e.preventDefault()
        dispatch(fetchCurrentWeather(city))
    }

    //  Search Box
    const showSearchBox = () => (
        <div className={styles.form__container}>
            <form className={styles.form__form} onSubmit={onSubmit}>
                <input className={styles.form__input} value={city} onChange={onCityChanged} placeholder="Search by city"></input>
                <button className={styles.form__submit} type="submit">Search</button>
            </form>
        </div>
    )

    // Weather Forecast for next 5 days
    const showForecastWeather = () => (
        <>
            <div className={styles.forecast__container}>
                {
                    forecast.map((item,index) => (
                        <DailyWeather 
                            item={item} 
                            key={index}
                            unixFormatter={unixFormatter}
                        >
                        </DailyWeather>
                    ))
                }
            </div>
        </>
    )

	return (
		<div className={styles.background}>  
           <div className={styles.container}>
                <img src={logo} alt="open weather" className={styles.logo}></img>
                {
                    showSearchBox()
                }
                {
                    loading ? 
                    <Loader></Loader> : 
                    <div className={styles.weatherCard}>
                        {
                            current && !error &&
                            (
                            <>
                                    <h2>{current.name} / {current.sys.country}</h2>
                                    <CurrentWeather current={current} unixFormatter={unixFormatter}></CurrentWeather>
                            </>
                            )
                        }
                        {
                            forecast && !error &&
                            showForecastWeather()
                        }
                        {
                            error &&
                            <div>City Not Found</div>
                        }
                    </div>
                }
           </div>
        </div>
	)
}

export default Weather
