import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "./Weather.module.css";
import {
    fetchCurrentWeather,
    fetchWeatherForecast
} from "./weatherSlice";

const Weather = () => {
	const [city,setCity] = useState('Yangon')
    const {current,loading,error,forecast} = useSelector(state => state.weather)
    const dispatch = useDispatch()

    const onCityChanged = (e) => {
        setCity(e.target.value)
    }

    const onSubmit = () => {
        dispatch(fetchCurrentWeather(city))
        // dispatch(fetchWeatherForecast(city))
    }

    useEffect(() => {
        dispatch(fetchCurrentWeather(city))
        // dispatch(fetchWeatherForecast(city))
     }, [dispatch]);


    const showCurrentWeather = () => (
        <>
            <div>
                {JSON.stringify(current.weather[0].main)}
                {JSON.stringify(current.weather[0].icon)}
                {JSON.stringify(current.weather[0].id)}
            </div>
            <div className={styles.weather__current}>
                <img src={`http://openweathermap.org/img/wn/${current.weather[0].icon}@2x.png`} alt="weather"></img>
                <h1>{city} {JSON.stringify(current.weather[0].description)} { current && current.main.temp} &#176;</h1>
            </div>
            <h6>{}</h6>
            <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
            <button className={styles.form__submit} onClick={onSubmit}>Search</button>
        </>
    )
    const showForecastWeather = () => (
        <>
            <div>
                {JSON.stringify(forecast)}
            </div>
        </>
    )
	return (
		<div className={styles.background}>  
            {
                current && !loading && !error &&
                showCurrentWeather()
            }
            {
                loading && 
                <div>Loading .....</div>
            }
            {
                error && 
                <div>Data not found ...!</div>
            }
        </div>
	)
}

export default Weather
