import React, { useState,useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import moment from "moment";
import loader from '../loader/Loader'
import styles from "./Weather.module.css";
import {
    fetchCurrentWeather,
    fetchWeatherForecast
} from "./weatherSlice";

const Weather = () => {

	const [city,setCity] = useState('Yangon')
    const [isTyping,setIsTyping] = useState(false)
    const {current,loading,error,forecast} = useSelector(state => state.weather)
    const dispatch = useDispatch()

    const unixFormatter = (dt,format) => {
        return moment(new Date(dt*1000)).format(format)
    }

    const onCityChanged = (e) => {
        setIsTyping(true)
        setCity(e.target.value)
    }

    const onSubmit = (e) => {
        e.preventDefault()
        setIsTyping(false)
        dispatch(fetchCurrentWeather(city))
    }

    useEffect(() => {
        dispatch(fetchCurrentWeather(city)).then(res => {
            const {coord} = res.payload.data
            dispatch(fetchWeatherForecast(coord))

        })
     }, [dispatch]);

    const showCurrentDate = () => (
        <div className={styles.date}>
            {unixFormatter(current.dt,'dddd, MMMM Do YYYY')}
        </div>
    )

     const showSearchBox = () => (
       <div>
            <form onSubmit={onSubmit}>
                <input className={styles.form__input} value={city} onChange={onCityChanged}></input>
            </form>
       </div>
     )

    const showCurrentWeather = () => (
        <div>
            <h2>{current.name}</h2>
            <div className={styles.current__iconContainer}>
                <img className={styles.current__icon} loading='lazy' src={`http://openweathermap.org/img/wn/${current.weather[0].icon}@2x.png`} alt="weather"></img>
            </div>
            <h1>{ current && current.main.temp} &#176;C</h1>
            <div className={styles.current__weather}>
                <h3>{current.weather[0].description}</h3>
            </div>
        </div>
    )

    // daily forecast item
    const showForecastWeather = () => (
        <>
            <div className={styles.forecast__container}>
                {
                    forecast.map((item,index) => (
                        <div className={styles.forecast__daily} key={index}>
                            <p>{unixFormatter(item.dt,'ddd')}</p>
                            <img className={styles.forecast__icon} src={`http://openweathermap.org/img/wn/${item.weather[0].icon}@2x.png`} alt="weather"></img>
                            <p>{item.temp.day} &#176;C</p>
                        </div>
                    ))
                }
            </div>
        </>
    )

	return (
		<div className={styles.background}>  
           <div className={styles.container}>
                {
                    current && showCurrentDate()
                }
                {
                    showSearchBox()
                }
                {
                    loading && 
                    loader()
                }
                {
                    !loading && current && !error &&
                    showCurrentWeather()
                }
                {
                    !loading && forecast && !error &&
                    showForecastWeather()
                }
                {
                    !loading && error && !isTyping &&
                    <div>Data not found for {city}...!</div>
                }
           </div>
        </div>
	)
}

export default Weather
